INSERT INTO `locales_quest` (`Id`, `Title_loc3`, `Details_loc3`, `Objectives_loc3`, `CompletedText_loc3`) VALUES
('799996', 'Die Faelschung', 'Findet die kleine Frau, die diese Faelschung in Umlauf gebracht hat.', '$C, scheinbar wurde eine Faelschung dieses wertvollen Gegenstands produziert.$B$BFindet dieses kleine Ungeziefer, dass diesen Gegenstand hergestellt hat.', 'Findet die kleine Frau in Dalaran.'),
('799997', 'Der Stein der kleinen Frau', 'Findet meinen Stein den ich verloren habe und bringt ihn mir zurueck.', 'Hallo $N, ich habe meinen Stein in der Seelenschmiede verloren als ich vor dem grausigen Verschlinger der Seelen davon rannte. Ich glaube er hat ihn an sich genommen. Koennt ihr ihn mir bitte zurueckholen? Vielleicht habe ich dann eine kleine Belohnung fuer Euch.', 'Es ist Eure Sache was ihr tut.'),
('799998', 'Der mysterioese Gegenstand', 'Findet den Mann der den mysterioesen Gegenstand besitzt.', 'Hey, ihr da.$BHabt ihr nichts zu tun?Ich haette da etwas fuer Euch. In letzter Zeit geht das Geruecht um, das ein Mann einen mysterioesen und seltenen Gegestand gefunden hat. Habt ihr das nicht mitbekommen? Anscheinend soll er sich in der Scherbenwelt befinden, aber auch das ist sehr wage. Ich glaube mich noch zu erinnern, dass der Bereich indem er sich befinden koennte, im Bereich des Nethersturm ist. Wenn Ihr ihn findet koenntet Ihr Euch von diesem Gegenstand ueberzeugen. Verzweifelt nicht zu schnell wenn ihr ihn nicht gleich am Anfang findet. Ich gebe Euch noch eine Replik des Gegenstandes mit. Diese hat leider keine besondere Macht. ', 'Es ist Eure Sache was ihr tut.'),
('799999', 'Der Gegenstand', 'Liefert diesen Gegenstand an einen Haendler in Dalaran.', '$N, Liefert diesen Gegenstand an einen Haendler in Dalaran. Geruechten zufolge steht dieser eine bestimmte Haendler im Untergrund von Dalaran. Schaut Euch dort doch einfach um und bringt im diesen Stein.', 'Bringt diesen Stein zu einem Haendler in Dalaran.'),
('800000', 'Der Anfang', 'Liefert 20 Goldbarren an mich um euren Ehrgeiz und Euer Interesse zu beweisen.', 'Guten Tag $N. Ich habe von einem Volk gehoert das in einem entlegenen Gebiet von Azeroth wohnt. Um mit diesem Volk in Kontakt treten zu koennen, bedarf es jedoch \r\neines kleinen Oboluses. Bringt mir 20 Goldbarren und ich werde Euch den Standort verraten.', 'Bringt die Goldbarren zurueck zu Thedrium Wyrmbraeu um den Standort zu erfahren.'),
('800001', 'Der Begin einer neuen Reise', 'Bringt den Astralen Kredit zu Extator Magista', 'Bringt den Astralen Kredit zu Extator Magista und zeigt ihm sofort Eure Bereitschaft sich Ihm anzuschliessen. Herr Magista befindet sich im Gebirge zwischen den Grizzlyhuegeln sowie dem Heulenden Fjord.', 'Bringt den Astralen Kredit zu Extator Magista.'),
('800002', 'Die erste Pruefung', 'Bringt mir 20 Abgeworfene Echsenhaeute, 20 Gorillafangzaehne,30 Seidenstoffe.', 'Dies ist Eure erste Pruefung. Es wird eine leichte Aufgabe sein. Bringt mir 20 Abgeworfene Echsenhaeute, 20 Gorillafangzaehne,30 Seidenstoffe. Wir muessen ein Zeichen setzen, dass trotz unserer aktuellen Lage mit uns zu rechnen ist.', 'Kehrt zu Extator Magista zurueck um Eure Belohnung zu erhalten.'),
('800003', 'Die zweite Pruefung', 'Toetet 10 gegnerische Spieler', 'Bei der zweiten Pruefung wird Eure Kampffaehigkeit gegen ebenbuertige Gegner auf die Probe gestellt. Tretet gegen 10 Spieler an um die Pruefung abzuschliessen.', 'Kehrt zurueck um die Pruefung abzuschliessen.'),
('800004', 'Die dritte Pruefung', 'Bringt mir 10 boreanische Leder und 10 Wollverbaende um mich zu unterstuetzen.', 'Mein Volk muss leiden, bringt mir daher die benoetigten Materialien, damit ich diese zu meinem Volk bringen kann.', 'Kehrt zurueck um Eure Belohnung abzuholen.'),
('800005', 'Die Aufnahme', 'Bringt die Schriftensammlung zu Destro MMOwning. Toetet zusaetzlich Geisselfuerst Tyrannus und den den Verschlinger der Seelen.', 'Bringt die Schriftensammlung zu Destro MMOwning. Ihr findet Ihn an der Kueste von Westfall. Aus seiner Sicht gesehen schaut er auf ein Schriffswrack. Meist hoert er im Wind die Schreie von Murlockreaturen. Zusaetzlich muesst ihr Geisselfuerst Tyrannus und den Verschlinger der Seelen zu toeten.', 'Destro Mowning ist erfreut Euch zu sehen. Toetet zusaetzlich Geisselfuerst Tyrannus, Den Verschlinger der Seelen sowie Sartharion.'),
('800006', 'Die Gesteinstinktur', 'Bringt 3 Sulfuronbloecke sowie 3000 Ehrenpunkte zu Sophie Mondhain', 'Wie ihr seht ist mein Mann mit der Worgenkrankheit befallen. Ich habe von einer Tinktur gehoert, fuer welche man in die Tiefen des geschmolzenen Kerns reissen muss.\r\nBringt mir von dort 3 Sulfuronbloecke und zusaetzlich 3000 Ehrenpunkte. Beachtet wohl, Sulfuronbloecke sind ein aeussert seltenes Gut. Dem Vernehmen nach sind die Sulfuronbloecke im Geschmolzenen Kern zu finden.', 'Sophie Mondhain erwartet Euch sehnsuechtig.'),
('800007', 'Die Essenz der ewigen Fuenf', 'Bringt die Essenzen der ewigen Fuenf zu Sophie Mondhain.', '$N,$BDie Tinktur der Sulforonbloecke war leider ein Fehlschlag. Ein  Vertrauter erzaehlte mir aber von Essenzen der ewigen Fuenf. Diese sollen ueber magische Heilkraefte verfuegen. Bitte helft mir und bringt mir diese 5 Essenzen. Die Essenzen der ewigen Fuenf sollen sich im Besitz der 5 grossen Weltendrachen befinden. Ein Name, an den ich mich errinern kann, war glaube ich Smariss.', 'Sophie Mondhain erwartet Euch sehnsuechtig.'),
('800009', 'PreQuest: Der Fall des verlorenen Herrschers', 'Hoert Euch die einleitende Erzaehlung von Exitare an.', '$N,$Bich habe eine wichtige Nachricht fuer Euch. Eine neue Bedrohung ist im Begriff unsere Welt zu bedrohen.', 'Ich warte auf Euch.'),
('800010', 'PreQuest: Der Fall des verlorenen Herrschers', 'Sammelt 200 Grobe Steine, 5 Hammelkoteletts, 2 Schlaeuche mit zwergischen Starkbier und toetet 10 Kuehe.', '$N,$Bdie ersten Schritte um das neue uebel aufzuhalten sind denkbar einfach. Bringt mir 5 Hammelkoteletts und 2 Schlaeuche mit zwergischen Starkbier. Ich habe naemlich Durst und Hunger. Waehrend ich mich verkoestige koennt Ihr mir gleich noch 200 grobe Steine bringen. Diese benoetigen wir fuer unser erstes Pulver, welches hoffentlich den gewuenschten Effekt ausloest.', 'Ich warte auf Euch.'),
('800011', 'PreQuest: Der Fall des verlorenen Herrschers', 'Meldet Euch bei Bryana Atkenur und bringt ihr die Tinktur.', '$N,$Bunsere Tinktur scheint gut zu sein. Wir muessen sie nun nur noch testen. Bringt die Tinktur zu Bryana Atkenur. Sie wird die Guete ueberpruefen. Seid aber bitte zuegig, den durch den Transport wird die Tinktur instabil.', 'Ich warte auf Euch.'),
('800012', 'Die Drachenmutter', 'Toetet Sindragosa', 'Hallo $N,toetet Sindragosa. Sindragosa ist die Mutter aller Drachen, die ihr schon bezwungen habt. Sie muss sterben damit ich die benoetigte Tinktur erstellen kann bzw sie wirksam werden kann.', 'Es ist Eure Sache was ihr tut.'),
('800013', 'Die Tinktur der 4 Elemente', 'Toetet die Verkoerperung der 4 Elemente.', 'Hallo $N,die 4 Elemente besitzen Maechte die wir fuer das erfolgreiche Einsetzen der Tinktur brauchen. Toetet sie daher alle und nehmt deren Kraft in Euch auf. Viel Erfolg fleissige Wanderer.', 'Es ist Eure Sache was ihr tut.'),
('800014', 'Ueberbringt die Tinkur meinem Mann', 'Bringt die Tinktur zu Destro Raisin.', 'Hallo $N, die Tinktur ist fertig und muss nun nur noch meinem Mann ueberbracht werden. Aber beeilt Euch.', 'Es ist Eure Sache was ihr tut.'),
('800500', 'Mehr Hilfe fuer das Volk', 'Liefert 100 Netherstoff und 75 Raptorkrallen an mich.', 'Wir haben einen taeglichen Bedarf an verschiedenen Guetern. Liefert 100 Netherstoff und 50 Raptorkrallen an mich.', 'Bringt Ihr mir die benoetigten Dinge?'),
('800501', 'Toetet den Verschlinger der Seelen ', 'Toetet den Verschlinger der Seelen', 'Dies Bedarf keiner langen Erklaerung! Toetet den Verschlinger der Seelen', 'Bringt Ihr mir die benoetigten Dinge?'),
('800502', 'Die neue Postlieferung', '$N bitte liefert diesen Stapel Briefe an Johann Orrig vor Orgrimmar.', 'Diese Briefe muessen unbedingt Johann Orrig zugestellt werden. Sie enthalten wichtige Nachrichten unserer Mitglieder des Stammes. Ihr findet ihn vor Orgrimmar. ', 'Bringt Ihr ihm die benoetigten Briefe?'),
('800503', 'Die neue Postlieferung', '$N bitte liefert diesen Stapel Briefe an Pi von Haar in Sturmwind.', 'Diese Briefe muessen unbedingt Pi von Haar in Sturmwind zugestellt werden. Sie enthalten wichtige Nachrichten unserer Mitglieder des Stammes. Ihr findet Pi von Haar auf dem Kathedralenplatz.', 'Bringt Ihr ihm die benoetigten Briefe?'),
('800504', 'Naxxramas Rally', '$N bitte toetet Saphiron und Kel´Thuzad.', '$N,$B bittet entledigt Euch dieser 2 Bestien. Sie zerstoeren unsere Doerfer und toeten unsere Kinder.', 'Fuehrt ihr meinen Auftrag aus?'),
('800505', 'Gepflegt zerlegt', 'Erledigt 20 Spieler im PVP', 'Noch niemand hat eine Kneipenschlaegerei geschadet. Also stuerzt euch mitten hinein.', 'Fuehrt ihr meinen Auftrag aus?'),
('800506', 'Zwergenfilet', '$N, toetet Magni Bronzebart', '$N, $B habt ihr Lust auf Zwergenfilet? Toetet Magni Bronzebart, den Koenig der verachtenswerten Zwerge.', 'Fuehrt ihr meinen Auftrag aus?'),
('800507', 'Hordenmassaker', '$N, toetet Thrall von Orgrimmar', '$N, $B habt ihr Lust auf ein Hordenmassaker? Toetet Thrall von Orgrimmar , den Hauptling der blutruenstigen Horde.', 'Fuehrt ihr meinen Auftrag aus?'),
('800508', 'Tot den Menschen', '$N, toetet Varian Wrynn', '$N, $B habt ihr Lust den Menschen Leid zuzufuegen? Toetet Varian Wrynn, den Koenig der hochnaessigen Menschen.', 'Fuehrt ihr meinen Auftrag aus?'),
('800509', 'Froststoff sammeln ', 'Sammelt 100 Froststoff', 'Wir benoetigen Froststoff zum Weben unserer Kleidung. Bitte helf uns dabei.', 'Bringt Ihr mir die benoetigten Dinge?'),
('800510', 'Orrig der dunkle Schatten ', 'Toetet Orrig den dunklen Schatten.', 'Wir koennen Orrig nicht weiter am Leben lassen. Er ist eine Gefahr fuer unser ganzes Volk. Vernichtet ihn. Geht zu Seruc von Lantus um zu Orrig zu gelangen.', 'Werdet ihr den Auftrag aufsfuehren?'),
('800511', 'Federn fuer mein Kleid', 'Bringt mir 30 raue Geiernfedern.', 'Bitte helft mir, ich benoetige neue Federn fuer mein Gefieder.', 'Werdet ihr den Auftrag aufsfuehren?'),
('800512', 'Sammelwut', 'Sammelt 100 Staehlerne Pfeilspitzen, 75 Netherstoff und taetet 10 Sonnenschuppenkreischer, 15 Fleischreisserhyjaenen, 20 Kluftmoosgiftspucker, 15 Blutsichelfelzhetzer.', 'Helft uns bitte beim Sammeln dieser wichtigen Gegenstaende. Wir benoetigen diese wirklich dringend.', 'Werdet ihr den Auftrag aufsfuehren?'),
('800513', 'Kupferpulver', 'Bringt mir 30 Kupferpulver', 'Ich beoetige Kupferpulver fuer meinen strahlenden Glanz des Gefieders', 'Werdet ihr den Auftrag aufsfuehren?'),
('800514', 'Weekly: Eiskronenzitadelle', 'Toetet den Lichkoenig in der Eiskronenzitadelle. Ihr werdet reich belohnt werden !', '', 'Werdet ihr den Auftrag aufsfuehren?'),
